        }
        $request = array(
            'account' => $params['account'],
            'symbol' => $market['symbol'],
        );
        $request['offset'] = ($params['offset'] !== null) ? $params['offset'] : 0;
        $request['limit'] = ($limit !== null) ? $limit : 100;
