        $action2 = array(
            'account' => 'dex',
            'name' => 'withdrawall',
            'data' => array(
                'account' => $accountName,
            ),
            'authorization' => array( $auth ),
        );
        $actions = array( $action1, $action2 );
        $request = array(
            'transaction' => array( $actions ),
        );
        $data = null;
        $retries = 10;
        while ($retries > 0) {
            try {
                $serResponse = $this->publicPostOrdersSerialize (array_merge($request));
                $result = $this->safe_value($serResponse, 'data', array());
                $tx = hexToBytes ($result['serialized_tx_hex']);
