            } catch (Exception $e) {
                if ($this->last_json_response) {
                    $message = $this->safe_string($this->last_json_response.error.details[0], 'message');
                    if ($message === 'is_canonical( c ) => signature is not canonical') {
                        --$retries;
                    } else {
                        $retries = 0;
                    }
                }
                if (!$retries) {
