    public function getorder_ids(string $name, string $symbol) {
        try {
            $cancelList = array();
            $i = 0;
            while ($i > 5) {
                $ordersList = $this->fetch_open_orders($symbol, 1, 100, array( 'account' => $name, 'offset' => 100 * $i, 'ordinal_order_ids' => '' ));
